import React, { memo, useEffect, useState } from "react";
import { useSelector } from "react-redux";
import icons from "../ultis/icon";
import 'chart.js/auto';
import { Chart } from 'react-chartjs-2';


const ChartRank = () => {
  const { RiPlayMiniFill } = icons;
  const { chart, rank } = useSelector((state) => state.app);
  const [data, setdata] = useState({});
  useEffect(() => {
    const labels = chart?.times?.filter(item => +item.hour % 2 === 0)?.array.forEach(element => {
      map
    });(item => item.hour);
    console.log(labels)
    const dataChart = []
    if (chart?.items) {
        for (let i = 0; i < 3; i++) {
            const filteredData = chart?.items[Object.keys(chart?.items)[i]]?.filter(item => +item.hour % 2 === 0)?.map((item) => item.counter);
            dataChart.push({
              data: filteredData
            });
          
        }

        setdata({labels,dataChart})
    }
  }, [chart]);
  return (
    <div className="flex p-9 relative  h-[200px] ">
      <div className="absolute top-4 bottom-8 left-9 right-9 bg-chart-bg rounded-md"></div>
      <div className="absolute top-4 bottom-8 left-9 right-9 bg-chart p-3 rounded-md flex flex-col">
        <div className="flex items-center gap-2">
          <h3 className="bg-gradient-to-r text-2xl font-bold bg-clip-text text-transparent from-[#ff9357] from-10% to-[#9100ff]">
            {rank?.title}
          </h3>
          <span className="p-[2px] rounded-full bg-white ">
            <RiPlayMiniFill size={18} />
          </span>
        </div>
        <div className="flex ">
          <div>chart left</div>
          <div>
            {console.log(data)}
            {data && <Chart type="line" data={data}/>}
            
            </div>
        </div>
      </div>
    </div>
  );
};

export default memo(ChartRank);
