import React, { memo } from "react";
import moment from "moment";
import 'moment/locale/vi';

const Songs = ({ thumbnail, artistsNames, releaseDate, title }) => {
  return (
    <div className="w-[31%] flex  items-center p-2 text-white cursor-pointer hover:bg-hover rounded-xl" >
      <div className="flex gap-3 items-center">
      <img
          src={thumbnail}
          alt="thumbnailM"
          className="h-[55px] w-[55px] rounded-md object-contain"
        />
      
      <div className="flex flex-col">
      <span className="text-sm font-medium">{`${title?.length > 25 ? `${title.slice(0,20)}...`:`${title}`}`}</span>
        <span className="text-xs text-main-100">{`${artistsNames?.length > 25 ? `${artistsNames.slice(0,20)}...`:`${artistsNames}`}`}</span>
        <span className="text-xs text-main-100" >{moment(releaseDate *1000).fromNow()}</span>
      </div>
      </div>
    </div>
  );
};

export default memo(Songs);
