import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import * as apis from "../../api";
const Playlist = () => {
  const { title, pid } = useParams();
  const [playList, setplayList] = useState(null);

  useEffect(() => {
    const fetchDataPlayList = async () => {
      const reponse = await apis.apiGetDetailPlayList(pid);
      if(reponse?.data.err === 0) {
      setplayList(reponse);
      }
    };
    fetchDataPlayList();
  }, [pid]);
  return (
    <div className="flex flex-col px-14 py-8  ">
      <div className="flex w-full gap-6">
        <div className="w-[30%] flex-none border border-red-600">
            <figure>
                <img
                />
            </figure>
        </div>
        <div className="flex-auto border border-red-600">list song</div>
      </div>
      <div className="">author</div>
    </div>
  );
};

export default Playlist;
