import React, { memo, useEffect, useState } from "react";
import { useSelector } from "react-redux";
import icons from "../ultis/icon";
import {Chart} from "chart.js/auto";
import { Line } from 'react-chartjs-2';
import * as apis from '../api'

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js'
ChartJS.register (
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
)



const ChartRank = () => {
  const { chart, rank } = useSelector(state => state.app);
  const { RiPlayMiniFill } = icons;
  const [data, setDataChart] = useState({ datasets: [] });
  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
    },
  };
  useEffect(() => {
    const dataChart = []
    if (chart?.items) {
      for (let i = 0; i < 3; i++) {
            dataChart.push({
              data: chart?.items[Object.keys(chart?.items)[i]]?.filter(item => +item.hour % 2 === 0)?.map(item => item.counter)
            });
        }

        setDataChart({
          labels : chart?.times?.filter(item => +item.hour % 2 === 0)?.map(item => item.hour),
          datasets : dataChart
        })
      }

  }, [chart]);

  return (
    <div className="flex p-9 relative">
      <div className="absolute top-4 bottom-8 left-9 right-9 bg-chart-bg rounded-md z-10"></div>
      <div className="absolute top-4 bottom-8 left-9 right-9 bg-chart p-3 rounded-md flex flex-col z-20">
        <div className="flex items-center gap-2">
          <h3 className="bg-gradient-to-r text-2xl font-bold bg-clip-text text-transparent from-[#ff9357] from-10% to-[#9100ff]">
            {rank?.title}
          </h3>
          <span className="p-[2px] rounded-full bg-white ">
            <RiPlayMiniFill size={18} />
          </span>
        </div>
        <div className="flex">
          <div className="flex-4 border border-white">chart left</div>
          <div className="flex-6 border border-white ">
            {console.log(data)}
            {data&&<Line  options={options} data={data}/>}
            </div>
        </div>
      </div>
    </div>
  );
};

export default memo(ChartRank);
