import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import * as apis from "../../api";
import moment from 'moment'
const Playlist = () => {
  const { pid } = useParams();
  const [playList, setplayList] = useState({});

  useEffect(() => {
    const fetchDataPlayList = async () => {
      const reponse = await apis.apiGetDetailPlayList(pid);
      if (reponse?.data.err === 0) {
        setplayList(reponse.data?.data);
      }
    };
    fetchDataPlayList();
  }, [pid]);
  console.log(playList)
  return (
    <div className="flex flex-col px-14 py-8  ">
      <div className="flex w-full gap-6">
        <div className="w-[30%] flex-none border border-red-600 flex flex-col items-center text-main-100 gap-2">
          <img
            src={playList?.thumbnailM}
            alt="thumbnail"
            className="rounded-lg"
          />
          div
        </div>
        <div className="flex-auto border border-red-600">list song</div>
      </div>
      <div className="">author</div>
    </div>
  );
};

export default Playlist;
