import actionType from "../actions/acitonType";


const initState = {
    banner : [],
}
const appReducer = (state = initState, action) => {
    switch (action.type) {
        case actionType.GET_HOME :
            
            return {
                ...state,
                banner: action.homeData?.find(item => item.secc)
            };
    
        default:
            return state;
    }
}
export default appReducer
