import React, { memo, useEffect, useState } from "react";
import { useSelector } from "react-redux";
import icons from "../ultis/icon";
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';


const ChartRank = () => {
  const { RiPlayMiniFill } = icons;
  const { chart, rank } = useSelector(state => state.app);
  console.log("sdas",chart, rank)
  const [data1, setDataChart] = useState(null);
  useEffect(() => {
    if (chart) {
      const labels = chart?.times?.filter(item => +item.hour % 2 === 0)
      const labelsy = labels && labels.length > 0 ? labels?.map(item => item.hour) : []
      const dataChart = []
      for (let i = 0; i < 3; i++) {
        const dataFill = chart?.items[Object.keys(chart?.items)[i]]?.filter(item => +item.hour % 2 === 0)
        const datax = dataFill && dataFill.length > 0 ? dataFill?.map(item => item.counter) :[]
            dataChart.push({
              data: datax
            });
        }
        setDataChart({labelsy,dataChart})
        console.log(labelsy,dataChart)
    }
  }, [chart]);

  console.log(data1)
  return (
    <div className="flex p-9 relative  h-[200px] ">
      <div className="absolute top-4 bottom-8 left-9 right-9 bg-chart-bg rounded-md"></div>
      <div className="absolute top-4 bottom-8 left-9 right-9 bg-chart p-3 rounded-md flex flex-col">
        <div className="flex items-center gap-2">
          <h3 className="bg-gradient-to-r text-2xl font-bold bg-clip-text text-transparent from-[#ff9357] from-10% to-[#9100ff]">
            {rank?.title}
          </h3>
          <span className="p-[2px] rounded-full bg-white ">
            <RiPlayMiniFill size={18} />
          </span>
        </div>
        <div className="flex">
          <div className="flex-4 border border-white">chart left</div>
          <div className="flex-6 border border-white">
            
            {data1?<Line  data={data1}/> : ''}
            </div>
        </div>
      </div>
    </div>
  );
};

export default memo(ChartRank);
