import { useEffect } from "react";
import { useSelector } from "react-redux";
import { getArrSlider } from "../ultis/fn";
const Slider = () => {
  const { banner } = useSelector((state) => state.app);

  useEffect(() => {
    const sliderEls = document.getElementsByClassName("slider-app");
    let sttStart = 0;
    let sttEnd = 2;
    const intervalId = setInterval(() => {
      const list = getArrSlider(sttStart, sttEnd, sliderEls.length - 1);
      for (let i = 0; i < sliderEls.length; i++) {
        //delete classname
        sliderEls[i].classList.remove(
          "animate-slide-right",
          "order-last",
          "z-20"
        );
        sliderEls[i].classList.remove(
          "animate-slide-left",
          "order-first",
          "z-10"
        );
        sliderEls[i].classList.remove("animate-slide-left2", "order-2", "z-10");

        if (list.some((item) => item === i)) {
          sliderEls[i].style.cssText = `display: block`;
        } else {
          sliderEls[i].style.cssText = `display: none`;
        }
      }
      list.forEach((item) => {
        if (item === sttEnd) {
          sliderEls[item].classList.add(
            "animate-slide-right",
            "order-last",
            "z-20"
          );
        } else if (item === sttStart) {
          sliderEls[item].classList.add(
            "animate-slide-left",
            "order-first",
            "z-10"
          );
        } else {
          sliderEls[item].classList.add(
            "animate-slide-left2",
            "order-2",
            "z-10"
          );
        }
      });
      sttStart =
        sttStart === sliderEls.length - 1 ? (sttStart = 0) : (sttStart += 1);
      sttEnd = sttEnd === sliderEls.length - 1 ? (sttEnd = 0) : (sttEnd += 1);
    }, 3000);

    return () => {
      intervalId && clearInterval(intervalId);
    };
  }, []);
  return (
    <div className="flex overflow-hidden gap-4 p-[32px]">
      <div className="w-1/3">
        {banner?.map((item, index) => (
          <img
            src={item.banner}
            className={`slider-app flex-1 gap-4 w-1/3 object-contain rounded-lg ${
              index <= 2 ? "block" : "hidden"
            }`}
            alt="slider"
            key={item.encodeId}
          />
        ))}
      </div>
    </div>
  );
};

export default Slider;
