import React, { memo, useEffect, useState } from "react";
import { useSelector } from "react-redux";
import icons from "../ultis/icon";
import {Chart} from "chart.js/auto";
import { Line } from 'react-chartjs-2';






const ChartRank = () => {
  const { chart, rank } = useSelector(state => state.app);
  const { RiPlayMiniFill } = icons;
  const [data, setDataChart] = useState({ datasets: [] });
  const options = {
    responsive: true,
    pointRadius :0 ,
    maintainAspectRatio :false,
    hoverRadius : 4,
    plugins: {
      legend: false

    },
    scales : {
      y : {
        ticks : {display:false},
        grid: {
          drawTicks : false,
          color :'gray',
          },
          min : chart?.minScore,
          max : chart?.maxScore,  
          border : {
            dash : [3, 4]
        },

      },
      x : {
        ticks : {color : 'white'},
        grid : {
          color : 'transparent'
        }
      }
    },
    hover : {
      // mode : dataChart,
      intersect : false
    }
  };
  useEffect(() => {
    const dataChart = []
    if (chart?.items) {
      for (let i = 0; i < 3; i++) {
            dataChart.push({
              data: chart?.items[Object.keys(chart?.items)[i]]?.filter(item => +item.hour % 2 === 0)?.map(item => item.counter),
              borderColor : i === 0 ? '#4a8ddf' : i === 1 ?  '#27bc9c' : "#e35050",
              tension :0.3,
              borderWidth :2,
            });
        }

        setDataChart({
          labels : chart?.times?.filter(item => +item.hour % 2 === 0)?.map(item => item.hour),
          datasets : dataChart
        })
      }

  }, [chart]);
console.log(chart)
  return (
    <div className="flex p-9 relative h-[350px] max-h-[350px]   ">
      <div className="absolute top-4 bottom-8 left-9 right-9 bg-chart-bg rounded-md max-h-[350px]"></div>
      <div className="absolute top-4 bottom-8 left-9 right-9 bg-chart p-3 rounded-md flex flex-col z-20">
        <div className="flex items-center gap-2">
          <h3 className="bg-gradient-to-r text-2xl font-bold bg-clip-text text-transparent from-[#ff9357] from-10% to-[#9100ff]">
            {rank?.title}
          </h3>
          <span className="p-[2px] rounded-full  ">
            <RiPlayMiniFill size={18} />
          </span>
        </div>
        <div className="flex">
          <div className="flex-3 ">chart left</div>
          <div className="flex-7 h-full">
            {console.log(data)}
            {data&&<Line  options={options} data={data}/>}
            </div>
        </div>
      </div>
    </div>
  );
};

export default memo(ChartRank);
