import React, { memo, useEffect, useState } from "react";
import { useSelector } from "react-redux";
import icons from "../ultis/icon";
import { Chart } from "chart.js/auto";
import { Line } from "react-chartjs-2";
import { Songs } from "./";

const ChartRank = () => {
  const { chart, rank } = useSelector((state) => state.app);
  const { RiPlayMiniFill } = icons;
  const [data, setDataChart] = useState({ datasets: [] });
  const options = {
    responsive: true,
    pointRadius: 0,
    maintainAspectRatio: false,
    plugins: {
      legend: false,
    },

    scales: {
      y: {
        ticks: { display: false, stepSize: 6000 },
        grid: {
          drawTicks: false,
          color: "rgba(255,255,255,.2)",
        },
        min: chart?.minScore,
        max: chart?.maxScore,
        border: {
          dash: [3, 4],
        },
      },
      x: {
        ticks: { color: "#ffffff80" },
        grid: {
          color: "transparent",
        },
      },
    },
    hover: {
      mode: "dataset",
      intersect: false,
    },
  };
  useEffect(() => {
    const dataChart = [];
    if (chart?.items) {
      for (let i = 0; i < 3; i++) {
        dataChart.push({
          data: chart?.items[Object.keys(chart?.items)[i]]
            ?.filter((item) => +item.hour % 2 === 0)
            ?.map((item) => item.counter),
          borderColor: i === 0 ? "#4a8ddf" : i === 1 ? "#27bc9c" : "#e35050",
          tension: 0.3,
          borderWidth: 1,
          pointBackgroundColor:
            i === 0 ? "#4a8ddf" : i === 1 ? "#27bc9c" : "#e35050",
          pointHoverRadius: 4,
          pointBorderColor: "white",
          pointHoverBorderWidth: 2,
        });
      }

      setDataChart({
        labels: chart?.times
          ?.filter((item) => +item.hour % 2 === 0)
          ?.map((item) => `${item.hour}:00`),
        datasets: dataChart,
      });
    }
  }, [chart]);
  console.log(rank, chart);
  return (
    <div className="flex p-9 relative h-[350px] max-h-[350px]   ">
      <div className="absolute top-4 bottom-9 left-9 right-9 bg-chart-bg rounded-md "></div>
      <div className="absolute top-4 bottom-9 left-9 right-9 bg-chart p-3 rounded-md flex flex-col ">
        <div className="flex items-center gap-2">
          <h3 className="bg-gradient-to-r text-2xl font-bold bg-clip-text text-transparent from-[#ff9357] from-10% to-[#9100ff]">
            {rank?.title}
          </h3>
          <span className="p-[2px] rounded-full bg-white  ">
            <RiPlayMiniFill size={18} />
          </span>
        </div>
        <div className="flex">
          <div className="flex-4 flex flex-col w-full p-2 gap-2 ">
            {rank?.items
              ?.filter((item, index) => index < 3)
              ?.map((item, index) => (
                <div key={item.encodeId} className="flex w-full rounded-md  bg-[#ffffff12] ">
                  <Songs
                    thumbnail={item.thumbnail}
                    title={item.title}
                    artistsNames={item.artistsNames}
                    encodeId={item.encodeId}
                    order={index + 1}
                    percent={Math.round((item.score / chart?.totalScore) * 100)}
                  />
                </div>
              ))}
          </div>
          <div className="flex-6 h-[90%]  w-full p-2 text-xs  ">
            {data && <Line options={options} data={data} />}
          </div>
        </div>
      </div>
    </div>
  );
};

export default memo(ChartRank);
