import React, { memo, useState } from "react";
import { useSelector } from "react-redux";
import { isPlay } from "../store/actions";
import { Songs } from "./";

const NewRelease = () => {
  const { newRelease } = useSelector((state) => state.app);
  const [isActive, setisActive] = useState(0);
  return (
    <div className="px-9 py-5 flex flex-col gap-2 ">
      <div className="flex justify-between items-center  pb-2 ">
        <h3 className="text-main text-xl font-bold">{newRelease?.title}</h3>
        <span className="text-xs font-medium text-main-100">TẤT CẢ</span>
      </div>
      <div className="flex gap-3 text-white">
        <button
          className={`rounded-xl border border-default text-xs px-5 py-1  ${
            !isActive && "bg-btn"
          }`}
          type="button"
        >
          TẤT CẢ
        </button>
        <button
          className={`rounded-xl border border-default text-xs px-5 py-1  ${
            isActive === 1 && "bg-btn"
          }`}
          type="button"
        >
          VIỆT NAM
        </button>
        <button
          className={`rounded-xl border border-default text-xs px-5 py-1  ${
            isActive === 2 && "bg-btn"
          }`}
          type="button"
        >
          QUỐC TẾ
        </button>
      </div>
      <div>
        {newRelease?.items?.all?.map((item) => (
            <Songs key={item.encodeId} data={item} />
          
        ))}
      </div>
    </div>
  );
};

export default memo(NewRelease);
