            dispatch(actions.isPlay(true))
          }
    }
    >
      {order && <span>{order}</span>}
      <div className="flex gap-3 items-center">
      <img
          src={thumbnail}
          alt="thumbnail"
          className="h-[55px] w-[55px] rounded-md object-contain"
        />
      
      <div className="flex flex-col">
        <span className="text-sm font-medium">{`${title?.length > 25 ? `${title.slice(0,20)}...`:`${title}`}`}</span>
        <span className="text-xs text-main-100">{`${artistsNames?.length > 25 ? `${artistsNames.slice(0,20)}...`:`${artistsNames}`}`}</span>
        {releaseDate &&<span className="text-xs text-main-100" >{moment(releaseDate *1000).fromNow()}</span> }
      </div>
      {percent && <span>{`${percent}%`}</span>}
      </div>
    </div>
  );
};

export default memo(Songs);
